.test-container {
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
}

.test-header {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 30px;
    border-radius: 10px;
    text-align: center;
    margin-bottom: 20px;
}

.test-card {
    background: white;
    border-radius: 8px;
    padding: 20px;
    margin-bottom: 15px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease;
}

.test-card:hover {
    transform: translateY(-5px);
}

.test-button {
    background-color: #ff6b6b;
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
    transition: background-color 0.3s ease;
}

.test-button:hover {
    background-color: #ff5252;
}

@media (max-width: 768px) {
    .test-container {
        padding: 10px;
    }

    .test-header {
        padding: 20px;
    }
}
